import './App.css';
import {Component} from "react";
import Button from '@mui/material/Button';
import axios from 'axios';

const ariaLabel = { 'aria-label': 'description' };

class App extends Component {
  state = {
    items: []
  };

  async componentDidMount() {
    const response = await fetch('http://localhost:8081/api/inventory/all');
    const body = await response.json();
    this.setState({items: body});
  }

  deleteProduct(id) {
    axios.delete('http://localhost:8081/api/inventory/'+id)
        .then(() => this.setState({ status: 'Delete successful' }));
  };

  render() {
    const {items} = this.state;

    return (
        <div className="App">
          <h1>Item List <Button variant="contained">Add</Button> </h1>
            {items.map(item =>
                <div key={item.id}>
                  <h4>Id:</h4> {item.id} <h4>Name:</h4> {item.product.name}  <h4>Description:</h4> {item.product.description} <Button variant="contained">Edit</Button> <Button onClick={() => {this.deleteProduct(item.id)}} variant="contained">Delete</Button>
                </div>
            )}
        </div>
    );
  }
}
export default App;
